function navScroll(){var e=window.pageYOffset;document.getElementById("navBar").style.boxShadow=100<e?(document.getElementById("navBar").style.backgroundColor="#fff",document.getElementById("navBar").style.transition="all .5s ease-in","1px 1px 10px rgba(0,0,0,.25)"):(document.getElementById("navBar").style.backgroundColor="transparent","none")}window.addEventListener("scroll",navScroll);var swiper=new Swiper(".swiper-container",{navigation:{nextEl:".swiper-button-next",prevEl:".swiper-button-prev"}});function menuOpener(){document.getElementById("megaMenu").classList.add("open")}function menuCloser(){document.getElementById("megaMenu").classList.remove("open")}var i,coll=document.getElementsByClassName("collapsible");for(i=0;i<coll.length;i++)coll[i].addEventListener("click",function(){this.classList.toggle("active");var e=this.nextElementSibling;e.style.maxHeight?e.style.maxHeight=null:e.style.maxHeight=e.scrollHeight+"px"});